const $ = new Env('京东边玩边赚');
const JD_API_HOST = 'https://api.m.jd.com';

const JD_DEFAULT_USER_AGENT = "jdapp;android;9.2.4;10;dcd95047f41ac162;network/wifi;model/CLT-AL01;addressid/352408213;aid/dcd95047f41ac162;oaid/33ff728d-de75-3a39-0f7d-f975dd762d96;osVer/29;appBuild/85576;psn/dcd95047f41ac162|489;psq/6;uid/dcd95047f41ac162;adk/;ads/;pap/JA2015_311210|9.2.4|ANDROID 10;osv/10;pv/499.6;jdv/0|kong|t_1000170135|tuiguang|notset|1605244079566|1606286683|1606304928;ref/com.jingdong.app.mall.home.JDHomeFragment;partner/huawei;apprpd/Home_Main;jdSupportDarkMode/0;Mozilla/5.0 (Linux; Android 10; CLT-AL01 Build/HUAWEICLT-AL01; wv) AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/77.0.3865.120 MQQBrowser/6.2 TBS/045224 Mobile Safari/537.36"

const notify = $.isNode() ? require('./sendNotify') : '';
let jdNotify = true;//是否关闭通知，false打开通知推送，true关闭通知推送
let cookiesArr = [], cookie = '', message = '';
const jdCookieNode = $.isNode() ? require('./jdCookie.js') : '';
if ($.isNode()) {
  Object.keys(jdCookieNode).forEach((item) => {
    cookiesArr.push(jdCookieNode[item])
  })
  if (process.env.JD_DEBUG && process.env.JD_DEBUG === 'false') console.log = () => {
  };
} else {
  cookiesArr.push(...[$.getdata('CookieJD'), $.getdata('CookieJD2')])
}
!(async () => {
  if (!cookiesArr[0]) {
    $.msg($.name, '【提示】请先获取京东账号一cookie\n直接使用NobyDa的京东签到获取', 'https://bean.m.jd.com/', {"open-url": "https://bean.m.jd.com/"});
    return;
  }
  for (let i = 0; i < cookiesArr.length; i++) {
    if (cookiesArr[i]) {
      cookie = cookiesArr[i];
      $.UserName = decodeURIComponent(cookie.match(/pt_pin=(.+?);/) && cookie.match(/pt_pin=(.+?);/)[1])
      $.index = i + 1;
      $.isLogin = true;
      $.nickName = '';
      message = '';
      console.log(`\n***********开始【京东账号${$.index}】${$.nickName || $.UserName}********\n`);
      if (!$.isLogin) {
        $.msg($.name, `【提示】cookie已失效`, `京东账号${$.index} ${$.nickName || $.UserName}\n请重新登录获取\nhttps://bean.m.jd.com/`, {"open-url": "https://bean.m.jd.com/"});

        if ($.isNode()) {
          await notify.sendNotify(`${$.name}cookie已失效 - ${$.UserName}`, `京东账号${$.index} ${$.UserName}\n请重新登录获取cookie`);
        } else {
          $.setdata('', `CookieJD${i ? i + 1 : ""}`);//cookie失效，故清空cookie。$.setdata('', `CookieJD${i ? i + 1 : "" }`);//cookie失效，故清空cookie。
        }
        continue
      }
      await playIndex();
    }
  }
})()
  .catch((e) => {
    $.log('', `❌ ${$.name}, 失败! 原因: ${e}!`, '')
  })
  .finally(() => {
    $.done();
  })

async function playIndex() {
  return new Promise(async resolve => {
    $.post(clientAction('playIndex', `{"client":"app","shareId":"","playId":"","source":""}`), async (err, resp, data) => {
      try {
        if (err) {
          console.log(`${JSON.stringify(err)}`)
          console.log(`${$.name} API请求失败，请检查网路重试`)
        } else {
          if (safeGet(data)) {
            // console.log(data)
            data = JSON.parse(data);
            if (data['code'] === '0') {
              let hotList = data['hot'];
              for (let i = 0; i < hotList.length; i++) {
                const vo = hotList[i];
                if (vo['status'] === 0 && vo['progress'] !== -1) {
                  console.log(`去玩【${vo['name']}】，获得${Number(vo['achieve'])}成就值：`);
                  await playAction(vo['playId']);
                }
              }
            } else {
              console.log(`异常：${JSON.stringify(data)}`)
            }
          }
        }
      } catch (e) {
        $.logErr(e, resp)
      } finally {
        resolve();
      }
    })
  })
}

function playAction(playId, client="app", type=1) {
  return new Promise(resolve => {
    $.post(clientAction('playAction', `{"client":"${client}","playId":"${playId}","type":"${type}"}`), (err, resp, data) => {
      try {
        if (err) {
          console.log(`\n${$.name}: API查询请求失败 ‼️‼️`)
          $.logErr(err);
        } else {
          // console.log(data)
          data = JSON.parse(data);
          if (data['code'] === '0') {
            console.log(`当前成就值：${Number(data['data']['achieve'])}，当前等级：${Number(data['data']['lv'])}`);
          }
        }
      } catch (e) {
        $.logErr(e, resp);
      } finally {
        resolve();
      }
    })
  })
}

function clientAction(functionId, body) {
  return {
    url: `${JD_API_HOST}/client.action`,
    headers: {
      'Cookie': cookie,
      'Host': 'api.m.jd.com',
      'User-Agent': $.isNode() ? (process.env.JD_USER_AGENT ? process.env.JD_USER_AGENT : $.getdata('JD_DEFAULT_USER_AGENT')) : ($.getdata('JDUA') ? $.getdata('JDUA') : "jdapp;iPhone;9.2.2;14.2;%E4%BA%AC%E4%B8%9C/9.2.2 CFNetwork/1206 Darwin/20.1.0"),
      'Referer': 'https://h5.m.jd.com/babelDiy/Zeus/3BB1rymVZUo4XmicATEUSDUgHZND/index.html?babelChannel=5&playname=%E8%BE%B9%E7%8E%A9%E8%BE%B9%E8%B5%9A&collectionId=527',
    },
    body : `functionId=${functionId}&body=${body}&client=wh5&clientVersion=1.0.0`
  }
}


async function showMsg() {
  return new Promise(async resolve => {
    let ctrTemp;
    if ($.isNode() && process.env.DREAMFACTORY_NOTIFY_CONTROL) {
      ctrTemp = `${process.env.DREAMFACTORY_NOTIFY_CONTROL}` === 'false';
    } else if ($.getdata('jdDreamFactory')) {
      ctrTemp = $.getdata('jdDreamFactory') === 'false';
    } else {
      ctrTemp = `${jdNotify}` === 'false';
    }
    if (ctrTemp) {
      $.msg($.name, '', message);
      if ($.isNode()) {
        await notify.sendNotify(`${$.name} - 账号${$.index} - ${$.nickName}`, `${message}`);
      }
    } else {
      $.log(`\n${message}\n`);
    }
    resolve()
  })
}

function safeGet(data) {
  try {
    if (typeof JSON.parse(data) == "object") {
      return true;
    }
  } catch (e) {
    console.log(e);
    console.log(`京东服务器访问数据为空，请检查自身设备网络情况`);
    return false;
  }
}


